import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { Input, Button, Grid } from 'semantic-ui-react';
import { ForceGraphContainer } from './ForceGraphContainer';
import api from '../api';
import './Main.css';
//import erdosData from '../data/erdos.json'; 

let nodes = [];
let links = [];

const mapStateToProps = state => ({
});

class Main extends Component {

    constructor(props) {
    super(props);
        this.authorClick = this.authorClick.bind(this);
        this.updateInputValue = this.updateInputValue.bind(this);
        this.state = {
            author: '',
            authors: [],
            //nodes: [],
            //links: [],
            isLoading: false,
        }
    };

    componentDidMount = async () => {
        this.setState({ isLoading: true })

        await api.getAllAuthors().then(authors => {
            this.setState({
                authors: authors.data.data,
                isLoading: false,
            }, () => {
                console.log("DATA LOADED");
                this.getErdosData();
            })
        })
    }

    getErdosData = () => {
        //  Read in the node and link data from the database
        let jsonData = this.convertToJSON(this.state.authors);
        //this.setState({nodes: jsonData.nodes});
        //this.setState({links: jsonData.links});
        nodes = jsonData.nodes;
        links = jsonData.links;
        //nodes = erdosData.nodes;
        //links = erdosData.links;
    }

    convertToJSON = (a) => {
        let nodes = []
        let links = []

        for(let i=0; i<a.length; i++) {
            let name = a[i].name;
            let id = a[i].vertex;
            let group = a[i].degree;
            let pubs = a[i].colaborators;
            let adj = a[i].adj;
            let data = {
                "id": id,
                "name": name,
                "group": group,
                "pubs": pubs
            };
            nodes.push(data);

            for(let j=0; j<adj.length; j++) {
                let source = id;
                let target = adj[j];
                let value = pubs;
                let data = {
                    "source": source,
                    "target": target,
                    "value": value,
                };
                links.push(data);
            }
        }

        let json = {
            'nodes': nodes,
            'links': links
        };
        return(json);
    }

    makeOptionList = () => {
        let buffer = [];
        for(let i=0; i<this.state.nodes.length; i++) {
            buffer.push(<option value={this.state.nodes[i].name} key={this.state.nodes[i].name}/>);
        }
        return(buffer);
    }

    authorClick = () => {
        //console.log("value of input field : "+this.state.author);
        this.props.dispatch({
            type: 'AUTHOR_SEARCH',
            payload: { data: this.state.author}
        })
    }

    clearAuthor = () => {
        /*this.setState({
                author: ''
            }, () => {
                console.log("value of input field : "+this.state.author);
            }
        )*/
    }

    updateInputValue = (evt) => {
        this.setState({author: evt.target.value});
    }

    render () {
        //this.getErdosData();

        return (
            <div>
                <Grid >
                    <Grid.Row> </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            <div className="Main-header">
                                <p>Erdos Number 1 Collaboration Graph Display </p>
                            </div>
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row columns={3}>
                        <Grid.Column>
                            <Button size='big'>
                                <Link to="/about">About</Link>
                            </Button>
                        </Grid.Column>
                        <Grid.Column></Grid.Column>
                        <Grid.Column>
                            <div>
                                <Input  id='authorSelection' 
                                        list='authors' 
                                        placeholder='Choose author...' 
                                        size='large'
                                        onChange={this.updateInputValue}
                                />
                                <datalist id='authors'>
                                    {this.makeOptionList()}
                                </datalist>
                                <Button size='large' onClick={this.clearAuthor}>Clear</Button>
                                <Button size='large' onClick={this.authorClick}>Find</Button>
                            </div>
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row >
                        <Grid.Column>
                            <div 
                                 style={{border:'1px solid white', width: '95%', float: 'none', margin: '0 auto', overflow: 'auto'}}>
                                <ForceGraphContainer linksData={this.state.links} 
                                                     nodesData={this.state.nodes} 
                                                     author={this.state.author} />
                            </div>
                        </Grid.Column>
                    </Grid.Row>

                </Grid>
            </div>
        );
    }
}

export default connect(mapStateToProps)(Main);
