import React, { Component } from 'react';
import * as d3 from 'd3';

class ForceGraph extends Component {

  force = () => {

    var svg = d3.select("svg"),
          width =+ svg.attr("width"),
          height =+ svg.attr("height");

  }

  render () {
      return (
        <div>
          {this.force()}
        </div>
      );
    };
}

export default ForceGraph;


