import React from "react";
import { runForceGraph } from "./ForceGraphGenerator";
import styles from "./forceGraph.module.css";

//export function ForceGraphContainer({ linksData, nodesData, nodeHoverTooltip }) {
export function ForceGraphContainer({ linksData, nodesData }) {
  const containerRef = React.useRef(null);

  React.useEffect(() => {
    let destroyFn;

    if (containerRef.current) {
      //const { destroy } = runForceGraph(containerRef.current, linksData, nodesData, nodeHoverTooltip);
      const { destroy } = runForceGraph(containerRef.current, linksData, nodesData);
      destroyFn = destroy;
    }

    return destroyFn;
  }, [linksData, nodesData]);

  return <div ref={containerRef} className={styles.container} />;
}
