import React from "react";
//import { useSelector } from 'react-redux';
import { runForceGraph } from "./ForceGraphGenerator";
import styles from "./forceGraph.module.css";

export function ForceGraphContainer({ linksData, nodesData, author }) {
  const containerRef = React.useRef(null);

  //const selected = useSelector(state => state.author);

  React.useEffect(() => {
    let destroyFn;
    if (containerRef.current) {
      const { destroy } = runForceGraph(containerRef.current, linksData, nodesData, author, authors);
      destroyFn = destroy;
    }

    return destroyFn;
  }, [linksData, nodesData, author]);

  return <div ref={containerRef} className={styles.container} />;
}
