import React, { Component } from 'react';
import * as d3 from 'd3';

//const w = document.getElementById("force").innerWidth;
//const h = document.getElementById("force").innerHeight;
const w = 1283;
const h = 404;

class BarGraph extends Component {

    componentDidMount = () => {
      let nodes = this.props.data.nodes;
      this.drawBarChart(nodes)
    }

    drawBarChart = (nodes)  => {
      const width = w;
      const height = h;
      const scale = 5;
      const svgCanvas = d3.select(this.refs.canvas)
                          .append('svg')
                          .attr('width', width)
                          .attr('height', height);
                          //.style('border', '1px solid #aaa');

      svgCanvas.selectAll(".bar")
                .data(nodes)
                .enter().append("rect")
                .attr('fill', '#0099ff')
                .attr("class", "bar")
                .attr('x', (datapoint, iteration) => iteration * scale)
                .attr("y", function(d) { return d.pubs; })
                .attr("width", 1)
                .attr("height", function(d) { return height - d.pubs; });

      svgCanvas.selectAll("text")
                .data(nodes)
                .enter().append("text")
                .attr('x', (datapoint, iteration) => iteration * 5)
                .attr("y", function(d) { return d.pubs + 2; })
                .text(dataPoint => nodes.name);        
    }

    render() { 
      return (
        <div ref="canvas"></div> 
      )
    };

}

export default BarGraph;
