import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Input, Grid } from 'semantic-ui-react';
import './Main.css';
import TabView from './TabView';
import erdosData from '../data/erdos.json'; 

const nodes = erdosData.nodes;

class Main extends Component {

    constructor(props) {
    super(props);
        this.state = {
        }
    };

    makeOptionList = () => {
        let buffer = [];
        for(let i=0; i<nodes.length; i++) {
            buffer.push(<option value={nodes[i].name} key={nodes[i].name}/>);
        }
        return(buffer);
    }

    render () {

        return (
            <div>
                <Grid >
                    <Grid.Row> </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            <div className="Main-header">
                                <p>Erdos Number 1 Collaboration Graph Display </p>
                            </div>
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row columns={3}>
                        <Grid.Column></Grid.Column>
                        <Grid.Column></Grid.Column>
                        <Grid.Column>
                            <div>
                                <Input list='authors' placeholder='Choose authour...' />
                                <datalist id='authors'>
                                    {this.makeOptionList()}
                                </datalist>
                            </div>
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row>
                        <div className="d3_tabs">
                        <Grid.Column>
                            <TabView />
                        </Grid.Column>
                        </div>
                    </Grid.Row>

                    <Grid.Row columns={2}>
                        <Grid.Column>
                            <Link to="/about">About</Link>
                        </Grid.Column>
                        <Grid.Column>
                            <Link to="/messages">Messages</Link>
                        </Grid.Column>
                    </Grid.Row>

                </Grid>
            </div>
        );
    }
}

export default Main;
