import React, { Component } from 'react';
import * as d3 from 'd3';

const w = 1283;
const h = 404;
//const h = document.getElementById("#force").innerHeight

/*const n = [ 
  { id: "mammal", group: 0, name: "Mammals", pubs: 1 },
  { id: "dog"   , group: 0, name: "Dogs"   , pubs: 2 },
  { id: "cat"   , group: 0, name: "Cats"   , pubs: 2 },
  { id: "fox"   , group: 0, name: "Foxes"  , pubs: 2 }, 
  { id: "elk"   , group: 0, name: "Elk"    , pubs: 2 },
  { id: "insect", group: 1, name: "Insects", pubs: 1 },
  { id: "ant"   , group: 1, name: "Ants"   , pubs: 2 },
  { id: "bee"   , group: 1, name: "Bees"   , pubs: 2 },  
  { id: "fish"  , group: 2, name: "Fish"   , pubs: 1 },
  { id: "carp"  , group: 2, name: "Carp"   , pubs: 2 },
  { id: "pike"  , group: 2, name: "Pikes"  , pubs: 2 } 
];

const l = [
  { target: "mammal", source: "dog" , value: 0.7 },
  { target: "mammal", source: "cat" , value: 0.7 },
  { target: "mammal", source: "fox" , value: 0.7 },
  { target: "mammal", source: "elk" , value: 0.7 },
  { target: "insect", source: "ant" , value: 0.7 },
  { target: "insect", source: "bee" , value: 0.7 },
  { target: "fish"  , source: "carp", value: 0.7 },
  { target: "fish"  , source: "pike", value: 0.7 },
  { target: "cat"   , source: "elk" , value: 0.1 },
  { target: "carp"  , source: "ant" , value: 0.1 },
  { target: "elk"   , source: "bee" , value: 0.1 },
  { target: "dog"   , source: "cat" , value: 0.1 },
  { target: "fox"   , source: "ant" , value: 0.1 },
  { target: "pike"  , source: "dog" , value: 0.1 }
];*/

class ForceGraph extends Component {

    componentDidMount = () => {
      let nodes = this.props.data.nodes;
      let links = this.props.data.links;
      //let nodes = n;
      //let links = l;
      this.drawForceGraph(nodes, links);
    }

    drawForceGraph = (nodes, links)  => {
      console.log(nodes);
      console.log(links);
      let width = w;
      let height = h;

      function ticked() {
        link
            .attr("x1", function(d) { return d.source.x; })
            .attr("y1", function(d) { return d.source.y; })
            .attr("x2", function(d) { return d.target.x; })
            .attr("y2", function(d) { return d.target.y; });

        node
            .attr("cx", function(d) { return d.x; })
            .attr("cy", function(d) { return d.y; });
      }

      function dragstarted(d) {
        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
            d.fx = d.x;
            d.fy = d.y;
      }

      function dragged(d) {
          d.fx = d3.event.x;
          d.fy = d3.event.y;
      }

      function dragended(d) {
          if (!d3.event.active) simulation.alphaTarget(0);
              d.fx = null;
              d.fy = null;
      }

      let svgCanvas = d3.select(this.refs.canvas)
                          .append('svg')
                          .attr('width', width)
                          .attr('height', height);

      let simulation =  d3.forceSimulation()
                          .force("link", d3.forceLink().id(function(d) { return d.id; }))
                          .force("charge", d3.forceManyBody())
                          .force("center", d3.forceCenter(width / 2, height / 2));

      let link = svgCanvas.append("g")
                          .attr("class", "links")
                          .selectAll("line")
                          .data(links)
                          .attr("stroke", "#aaa")
                          .enter().append("line")
                          .attr("stroke-width", function(d) { return Math.sqrt(d.value) / 4; });

      let node = svgCanvas.append("g")
                          .attr("class", "nodes")
                          .selectAll("circle")
                          .data(nodes)
                          .enter().append("circle")
                          .attr("r", function(d) {
                                      return Math.sqrt(d.pubs)*1.5;
                          })
                          .style("stroke", "gray")
                          .attr("fill", "#0099ff")
                          .call(d3.drag()
                          .on("start", dragstarted)
                          .on("drag", dragged)
                          .on("end", dragended));

      node.append("title")
          .text(function(d) {
              let dc = '';
              let name = ';'
              if (d.name.indexOf('*') !== -1) {
                name = d.name.replace('*', '');
                dc = ' (DECEASED)';
              }
              else {
                name = d.name;
              }
              let p = name.split(',');
              //name = p[1].concat(' ');
              name = name.concat(p[0]);
              name = name.concat(dc);
              let text = name.concat("\n ");
              text = text.concat(d.pubs);
              text = text.concat(" Coauthor(s) with Erdos number 2");
              return text;
          });

      simulation.nodes(nodes)
                .on("tick", ticked);

      simulation.force("link")
                .links(links);

    }

    render() { 

      return (
      <div ref="canvas"></div> 
      )
    };

}

export default ForceGraph;
