import React, { Component } from 'react';
import * as d3 from 'd3';


class ForceGraph extends Component {

    componentDidMount = () => {
      let nodes = this.props.data.nodes;
      let links = this.props.data.links;

      console.log(nodes);
      console.log(links);

      const data = [ 2, 4, 2, 6, 8 ]
      this.drawForceGraph(data)
    }

    drawForceGraph = (data)  => {

      //const width = document.getElementById("force").innerWidth;
      //const height = document.getElementById("force").innerHeight;
      const width = 1000;
      const height = 400;
      const scale = 20;
      const svgCanvas = d3.select(this.refs.canvas)
                          .append('svg')
                          .attr('width', width)
                          .attr('height', height)
                          .style('border', '1px solid black');

    svgCanvas.selectAll('rect')
              .data(data).enter()
              .append('rect')
              .attr('width', 40)
              .attr('height', (datapoint) => datapoint * scale)
              .attr('fill', 'orange')
              .attr('x', (datapoint, iteration) => iteration * 45)
              .attr('y', (datapoint) => height - datapoint * scale);

    svgCanvas.selectAll('text')
              .data(data).enter()
                  .append('text')
                  .attr('x', (dataPoint, i) => i * 45 + 10)
                  .attr('y', (dataPoint, i) => height - dataPoint * scale - 10)
                  .text(dataPoint => dataPoint);

    }

    render() { 

      return (
      <div ref="canvas"></div> 
      )
    };

}

export default ForceGraph;
