import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Dropdown, Input, Grid, Segment, Tab } from 'semantic-ui-react';
import './Main.css';

const REACT_VERSION = React.version;

const panes = [
    { menuItem: 'Force Display', render: () => <Tab.Pane>Force Display</Tab.Pane> },
    { menuItem: 'Bar Display', render: () => <Tab.Pane>Bar Display</Tab.Pane> },
  ]

class Main extends Component {

    constructor(props) {
    super(props);
        this.state = {
        }
    };

    componentDidMount() {
    };

    render () {

        return (
            <div>
                <Grid columns='equal'>
                    <Grid.Row>
                        <Grid.Column>
                            <div className="Main-header">
                                <p>Erdos Number Display </p>
                            </div>
                        </Grid.Column>

                        <Grid.Column>
                            <Dropdown
                                text='Filter'
                                icon='filter'
                                floating
                                labeled
                                button
                                className='icon'
                            >
                                <Dropdown.Menu>
                                <Dropdown.Header content='Search Issues' />
                                <Input icon='search' iconPosition='left' name='search' />
                                <Dropdown.Header icon='tags' content='Filter by tag' />
                                <Dropdown.Divider />
                                <Dropdown.Item
                                    label={{ color: 'red', empty: true, circular: true }}
                                    text='Important'
                                />
                                <Dropdown.Item
                                    label={{ color: 'blue', empty: true, circular: true }}
                                    text='Announcement'
                                />
                                <Dropdown.Item
                                    label={{ color: 'black', empty: true, circular: true }}
                                    text='Discussion'
                                />
                                </Dropdown.Menu>
                            </Dropdown>
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row>
                        <Grid.Column>
                            <Tab panes={panes} />
                        </Grid.Column>
                    </Grid.Row>

                    <Grid.Row>
                        <Grid.Column>
                            <Link to="/about">About</Link>
                        </Grid.Column>
                        <Grid.Column>
                            <Link to="/messages">Messages</Link>
                        </Grid.Column>
                    </Grid.Row>

                </Grid>
            </div>
        );
    }
}

export default Main;
