import React from 'react';
import { Tab, Segment } from 'semantic-ui-react';
import './TabView.css';
import ForceGraph from './ForceGraph';
import BarGraph from './BarGraph';
import erdosData from '../data/erdos.json';

const TabView  = () => {   
    const panes = [
        { 
            menuItem:   'Force Display', 
                        render: () => 
                            <Tab.Pane>
                                <Segment>
                                    <div id={'force'} >
                                        <ForceGraph data={erdosData}/>
                                    </div>
                                </Segment>
                            </Tab.Pane> 
        },
        { 
            menuItem:   'Bar Display', 
                        render: () => 
                            <Tab.Pane>
                                <Segment>
                                    <div id={'bar'}>
                                        <BarGraph data={erdosData}/>
                                    </div>
                                </Segment>
                            </Tab.Pane> 
        }
    ];

    return (
        <div>
            <Tab panes={panes} />
        </div>
    );

}
export default TabView;
