import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import { Input, Button, Grid, GridColumn } from 'semantic-ui-react';
import { ForceGraphContainer } from './ForceGraphContainer';
import api from '../api';
import erdosData from '../data/erdos.json';

//let nodes = erdosData.nodes;
//let links = erdosData.links;
//let nodes = [];
//let links = [];
let author = '';
let authors = [];

export function PWS(a) {
    console.log(a);
    /*const [authors, setAuthors] = useState({});
    const loadData = async () => {
        const res = await api.getAllAuthors();
        setAuthors(await convertToJSON(res.data.data));
      };

    useEffect(() => {
        loadData();
        return () => {};
      }, []);*/

    return (
        <div>
            <Grid >
                <Grid.Row></Grid.Row>
                <Grid.Row>
                    <Grid.Column>
                        <div className="Main-header">
                            <p>Erdos Number 1 Collaboration Graph Display </p>
                        </div>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row columns={3}>
                    <Grid.Column>
                        <Button size='big'>
                            <Link to="/about">About</Link>
                        </Button>
                    </Grid.Column>
                    <Grid.Column></Grid.Column>
                    <Grid.Column>
                        <div>
                            <Input  id='authorSelection' 
                                    list='authors' 
                                    placeholder='Choose author...' 
                                    size='large'
                            />
                            <datalist id='authors'>
                            </datalist>
                            <Button size='large' >Clear</Button>
                            <Button size='large' >Find</Button>
                        </div>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row >
                    <Grid.Column>
                        <div style={{border:'1px solid green', width: '95%', float: 'none', margin: '0 auto', overflow: 'auto'}}>
                            <ForceGraphContainer author={author} 
                                                 authors={authors} />
                            {/*<ForceGraphContainer linksData={links} 
                                                 nodesData={nodes} 
                                                 author={author} 
                                                 authors={authors} />*/}
                        </div>
                    </Grid.Column>
                </Grid.Row>

            </Grid>
        </div>
    );
}

function convertToJSON(a) {
    let nodes = []
    let links = []

    for(let i=0; i<a.length; i++) {
        let name = a[i].name;
        let id = a[i].vertex;
        let group = a[i].degree;
        let pubs = a[i].colaborators;
        let adj = a[i].adj;
        let data = {
            "id": id,
            "name": name,
            "group": group,
            "pubs": pubs
        };
        nodes.push(data);

        for(let j=0; j<adj.length; j++) {
            let source = id;
            let target = adj[j];
            let value = pubs;
            let data = {
                "source": source,
                "target": target,
                "value": value,
            };
            links.push(data);
        }
    }

    let json = {
        'nodes': nodes,
        'links': links
    };
    return(json);
}
