import React, { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import { Input, Button, Grid, GridColumn } from 'semantic-ui-react';
import { ForceGraphContainer } from './ForceGraphContainer';
import api from '../api';
import erdosData from '../data/erdos.json';

let nodes = erdosData.nodes;
let links = erdosData.links;
let author = '';

export function PWS() {
    const [authors, setAuthors] = useState({});
    const loadData = async () => {
        const res = await api.getAllAuthors();
        setAuthors(await res.data.data);
      };

    useEffect(() => {
        loadData();
        return () => {};
      }, []);

    return (
        <div>
            <p>{authors[0]['name']}</p>
            <Grid >
                <Grid.Row></Grid.Row>
                <Grid.Row>
                    <Grid.Column>
                        <div className="Main-header">
                            <p>Erdos Number 1 Collaboration Graph Display </p>
                        </div>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row columns={3}>
                    <Grid.Column>
                        <Button size='big'>
                            <Link to="/about">About</Link>
                        </Button>
                    </Grid.Column>
                    <Grid.Column></Grid.Column>
                    <Grid.Column>
                        <div>
                            <Input  id='authorSelection' 
                                    list='authors' 
                                    placeholder='Choose author...' 
                                    size='large'
                            />
                            <datalist id='authors'>
                            </datalist>
                            <Button size='large' >Clear</Button>
                            <Button size='large' >Find</Button>
                        </div>
                    </Grid.Column>
                </Grid.Row>

                <Grid.Row >
                    <Grid.Column>
                        <div 
                                style={{border:'1px solid green', width: '95%', float: 'none', margin: '0 auto', overflow: 'auto'}}>
                            <ForceGraphContainer linksData={links} 
                                                    nodesData={nodes} 
                                                    author={author} />
                        </div>
                    </Grid.Column>
                </Grid.Row>

            </Grid>
        </div>
    );
}
