import React from "react";
import { runForceGraph } from "./ForceGraphGenerator";
import styles from "./forceGraph.module.css";

export function ForceGraphContainer({ author, authors }) {
//export function ForceGraphContainer({ linksData, nodesData, author, authors }) {
  const containerRef = React.useRef(null);

  React.useEffect(() => {
    let destroyFn;
    if (containerRef.current) {
      //const { destroy } = runForceGraph(containerRef.current, linksData, nodesData, author);
      const { destroy } = runForceGraph(containerRef.current, authors.links, authors.nodes, author);
      destroyFn = destroy;
    }

    return destroyFn;
  }, [author, authors]);
  //}, [linksData, nodesData, author, authors]);

  return <div ref={containerRef} className={styles.container} />;
}
