import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import { Dropdown, Button, Grid } from 'semantic-ui-react';
import queryString from 'query-string';
import './Main.css';
import logo from '../../images/aerosort.png';

import Search from "./searchComponent/Search";

class Main extends Component {

  constructor(props) {
    super(props);
    this.state = {
      artifactsList: [],
      artifactValue: ''
    }
  };

  componentDidMount() {
    var repository = queryString.parse(this.props.location.search).repo.toLowerCase();
    //var url = "http://ser01.aero.org:5000/artifact_types?repository=" +
    //          repository;
    var url = "http://c2ser0.aero.org:5000/artifact_types?repository=" +
              repository;
    let results = [];
    try {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                results = data.result.map( (r) => { return r } );
                this.setState({
                    artifactsList: results.map( (d) => {return {text: d, value: d}} )
                });
        });
    }
    catch (error) {
        console.error(error);
    }
  };

  onArtifactChange = (event, {value}) => {
    var v = value.toLowerCase().replace(/ /g,"_").replace(/\./g,"")
    this.setState({ artifactValue: v } );
  };

  handleSelect = () => {
    var newUrl = window.location.href + "&artifact=" + this.state.artifactValue;
    window.location.replace(newUrl);
  };

  queryValues = queryString.parse(this.props.location.search);

  renderSwitch = (values) => {
      if(values.artifact) {
        const searchView = (props) => <Search {...props} />

        switch(values.type) {
          case 'search':
            return (
              <div id="typeSelection">
                <div className = "TypeContainer">
                  <div className="Type">
                    <Router>
                      <Switch>
                        <Route exact path='/' render = {searchView} />
                      </Switch>
                    </Router>
                  </div>
                </div>
              </div>
            )
            case 'submit':
              console.log("Submit");
              return (
                <div id="typeSelection">
                  <label>{ values.type }</label>
                </div>
              )
          default:
            return (
              <div>
                <h4><label>What of action do you want (SEARCH or SUBMIT)?</label></h4>
              </div>
            );
        }
      } else {
        const { value } = this.state;
        return(
          <div style={{width: '90%', margin: 'auto', align: 'center'}}>
                  <span style={{float: 'center'}}>
                    <b>Choose an Artifact:&nbsp;&nbsp;</b>
                    <Dropdown id={'att'}
                          selection
                          value={value}
                          options={this.state.artifactsList}
                          onChange={this.onArtifactChange}
                          placeholder='Selection' />
                    <b>&nbsp;&nbsp;</b>
                    <Button type='submit'
                            style={{color: '#270188'}}
                            disabled={!(this.state.artifactValue)}
                            onClick={this.handleSelect}>
                            Select
                    </Button>
                  </span>
          </div>
        );
      }
  }

  artifactSelect = (values) => {
    if(values.artifact) {
      return (
          <Button type='submit'
              onClick={this.handleArtifactSelect}>
              Select Artifact
          </Button>
      );
    }
  }

  handleArtifactSelect = () => {
    var p = window.location.href.split('&');
    var newUrl = p[0] + '&' + p[1];
    window.location.replace(newUrl);
  };

  render () {
    let repo = this.queryValues.repo.charAt(0).toUpperCase() + this.queryValues.repo.slice(1);
    repo = repo.replace(/_/g, ' ');
    repo = repo.toLowerCase()
               .split(' ')
               .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
               .join(' ');

    return (
      <div className="Main">
        <table width = "100%" rules="none">
          <tbody>
            <tr bgcolor = "#270188">
                <td> 
                  <Grid verticalAlign={'middle'}>
                    <Grid.Row>
                      <Grid.Column width={3}></Grid.Column>
                      <Grid.Column width={10}>
                        <font color="white"><img src={logo} className="App-logo" alt="logo" /></font>
                      </Grid.Column>
                      <Grid.Column width={3}>
                        <div style={{align: 'right'}}>
                          {this.artifactSelect(this.queryValues)}
                        </div>
                      </Grid.Column>
                    </Grid.Row>
                  </Grid>
                </td>
            </tr>
            <tr>
              <td colSpan="2">&nbsp;</td>
            </tr>
            <tr>
              <td colSpan="2">
                  <p><font size="20" color="black">{repo}</font></p>
              </td>
            </tr>
            <tr>
              <td colSpan="2">&nbsp;</td>
            </tr>
            <tr valign = "top">
                <td colSpan="2">
                  <div>
                    {this.renderSwitch(this.queryValues)}
                  </div>     
                </td>
            </tr>
            <tr>
              <td height="150" colSpan="2"></td>
            </tr>
          </tbody>
          <tfoot>
            <tr>
                <td bgcolor = "#270188" colSpan="2">
                  <font color="white">
                    <center>
                        Copyright © 2020 The Aerospace Corporation.  All rights reserved.
                    </center>
                  </font>
                </td>
            </tr>
          </tfoot>
        </table>
      </div> 
    );
  }
}

export default Main;
