import React, { useState } from 'react';
import { Tab, Segment } from 'semantic-ui-react';
import './TabView.css';
import ForceGraph from './ForceGraph';
import BarGraph from './BarGraph';
import erdosData from '../data/erdos.json';

const TabView  = () => {    
    const panes = [
        { 
            menuItem:   'Force Display', 
                        render: () => 
                            <Tab.Pane>
                                <Segment>
                                    <ForceGraph erdosData={erdosData}/>
                                </Segment>
                            </Tab.Pane> 
        },
        { 
            menuItem:   'Bar Display', 
                        render: () => 
                            <Tab.Pane>
                                <Segment >
                                    <BarGraph erdosData={erdosData}/>
                                </Segment>
                            </Tab.Pane> 
        }
    ];

    return (
        <div>
            <Tab panes={panes} />
        </div>
    );

}
export default TabView;
