import React, { Component } from 'react';
import * as d3 from 'd3';

class ForceGraph extends Component {

  force = () => {
  width = window.innerWidth;
  height = window.innerHeight;
  svg =   d3.select('svg')
              .attr('width', this.width)
              .attr('height', this.height);
  simulation =  d3.forceSimulation()
              .force("link", d3.forceLink().id(function(d) { return d.id; }))
              .force("charge", d3.forceManyBody())
              .force("center", d3.forceCenter(this.width / 2, this.height / 2));

  link =  this.svg.append("g")
            .attr("class", "links")
            .selectAll("line")
            .data(this.props.data.links)
            .enter().append("line")
            .attr("stroke-width", function(d) { return Math.sqrt(d.value) / 4; });

  node =  this.svg.append("g")
            .attr("class", "nodes")
            .selectAll("circle")
            .data(this.props.data.nodes)
            .enter().append("circle")
            .attr("r", function(d) {
                        return Math.sqrt(d.pubs)*1.5;
            })
            .attr("fill", "#0099ff")
            .call(d3.drag()
            .on("start", this.dragstarted)
            .on("drag", this.dragged)
            .on("end", this.dragended));

  node = this.node.append("title")
            .text(function(d) {
                let dc = '';
                let name = '';
                if (d.name.indexOf('*') != -1) {
                  name = d.name.replace('*', '');
                  dc = ' (DECEASED)';
                }
                else {
                  name = d.name;
                }
                let p = name.split(',');
                name = p[1].concat(' ');
                name = name.concat(p[0]);
                name = name.concat(dc);
                let text = name.concat("\n ");
                text = text.concat(d.pubs);
                text = text.concat(" Coauthor(s) with Erdos number 2");
                return text;
            });

  simulation =  this.simulation
                    .nodes(this.props.data.nodes)
                    .on("tick", this.ticked);

  simulation =  this.simulation.force("link")
                    .links(this.props.data.links);
  }

  ticked = () => {
      let link = link
          .attr("x1", function(d) { return d.source.x; })
          .attr("y1", function(d) { return d.source.y; })
          .attr("x2", function(d) { return d.target.x; })
          .attr("y2", function(d) { return d.target.y; });

      let node = node
          .attr("cx", function(d) { return d.x; })
          .attr("cy", function(d) { return d.y; });
    }

  dragstarted = (d) => {
    if (!d3.event.active) this.simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
  }

  dragged = (d) => {
      d.fx = d3.event.x;
      d.fy = d3.event.y;
  }

  dragended = (d) => {
      if (!d3.event.active) this.simulation.alphaTarget(0);
          d.fx = null;
          d.fy = null;
  }  



  render () {
      let data = this.props.data;
      console.log(data.nodes);
      console.log(data.links);

      return (
        <div>
          {this.force(data)}
        </div>
      );
    };
}

export default ForceGraph;


