import React, { Component } from 'react';
import * as d3 from 'd3';

const w = 1283;
const h = 404;

class BarGraph extends Component {

    componentDidMount = () => {
      let nodes = this.props.data.nodes;
      //console.log(nodes);
      const data = [ 2, 4, 2, 6, 8 ];
      this.drawBarChart(data, nodes)
    }

    drawBarChart = (data, nodes)  => {

      //const width = document.getElementById("force").innerWidth;
      //const height = document.getElementById("force").innerHeight;
      const width = w;
      const height = h;
      const scale = 5;
      const svgCanvas = d3.select(this.refs.canvas)
                          .append('svg')
                          .attr('width', width)
                          .attr('height', height)
                          .style('border', '1px solid #aaa');

        /*svgCanvas.selectAll('rect')
                  .data(data).enter()
                  .append('rect')
                  .attr('width', 5)
                  .attr('height', (datapoint) => datapoint * scale)
                  .attr('fill', '#0099ff')
                  .attr('x', (datapoint, iteration) => iteration * 45)
                  .attr('y', (datapoint) => height - datapoint * scale);*/

        svgCanvas.selectAll('rect')
                  .data(nodes).enter()
                  .append('rect')
                  .attr('width', 5)
                  .attr('height', (datapoint) => datapoint * scale)
                  .attr('fill', '#0099ff')
                  .attr('x', (datapoint, iteration) => iteration * 45)
                  .attr('y', ([0, d3.max(nodes, function(d) { return d.pubs})]) );


        /*svgCanvas.selectAll('text')
                  .data(data).enter()
                      .append('text')
                      .attr('x', (dataPoint, i) => i * 45 + 10)
                      .attr('y', (dataPoint, i) => height - dataPoint * scale - 10)
                      .text(dataPoint => dataPoint);
        */
    }

    render() { 

      return (
      <div ref="canvas"></div> 
      )
    };

}

export default BarGraph;
